import 'package:abg_utils/abg_utils.dart';
import 'package:cloud_firestore/cloud_firestore.dart';

Future<String?> repairBlog() async{
  try{
    double percentage = 0;
    var oneStep = 100/_blog.length;

    for (var item in _blog){

      var _data = item.toJson();
      var t = await FirebaseFirestore.instance.collection("blog").doc(item.id).get();

      dprint("set id=${item.id}");
      await FirebaseFirestore.instance.collection("blog").doc(item.id).set(_data);

      if (!t.exists){
        dprint("Not exist. +1");
        await FirebaseFirestore.instance.collection("settings").doc("main")
            .set({"blog_count": FieldValue.increment(1)}, SetOptions(merge:true));
      }
      percentage += oneStep;
      dprint("успешно ${percentage.toStringAsFixed(0)}%");
    }
  }catch(ex){
    return "repairBlog " + ex.toString();
  }
  return null;
}


